import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import FilterPanel from '../components/FilterPanel';
import CarCard from '../components/CarCard';
import '../Style.K/styles.css';

const Home = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch cars from backend
  useEffect(() => {
    const fetchCars = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/inventory');
        setCars(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching inventory data');
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  // Handle car filtering
  const handleFilter = async (filters) => {
    try {
      const response = await axios.get('http://localhost:8080/api/inventory/search', {
        params: filters,
      });
      setCars(response.data);
    } catch (err) {
      setError('Error applying filters');
    }
  };

  // Navigate to car details page
  const handleViewDetails = (car) => {
    navigate('/car-details', { state: { car } });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="App">
      <Navbar />
      <div className="banner">
        <img
          src="https://images.unsplash.com/photo-1503376780353-7e6692767b70"
          alt="Banner"
          className="Custbanner-image"
        />
      </div>
      <div className="container">
        <FilterPanel onFilter={handleFilter} />
        <div className="car-list">
          {cars.map((car) => (
            <CarCard key={car.id} car={car} onViewDetails={() => handleViewDetails(car)} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
